const Hamburger = styled.div`
  position: absolute;
  cursor: pointer;
  justify-content: start;
  align-items: center;
  display: flex;
  width: 120px;
  height: 40px;
  border-radius: 4px;
  background: rgb(1, 134, 203);
  left: 2%;
  margin-top: 2%;
  transform: translate() (-50%, -5%);
  z-index: 2;
  box-shadow: rgba(60, 64, 67, 0.3) 0px 1px 2px 0px,
    rgba(60, 64, 67, 0.15) 0px 1px 3px 1px;
`;
const Nav = styled.div`
  position: relative;
`;
