import * as React from "react";
import { useRef } from "react";
import { motion, useCycle } from "framer-motion";
import { useDimensions } from "./use-dimensions";
import { MenuToggle } from "./MenuToggle";
import { Navigation } from "./Navigation";
import beneface from "./IMG/beneface.jpg";
import klas from "./IMG/class.jpg";
import dwl from "./IMG/download.svg";
import "./button.css";

const sidebar = {
  open: (height = 1000) => ({
    clipPath: `circle(${height * 2 + 200}px at 40px 40px)`,
    transition: {
      type: "spring",
      stiffness: 20,
      restDelta: 2
    }
  }),
  closed: {
    clipPath: "circle(30px at 40px 40px)",
    transition: {
      delay: 0.5,
      type: "spring",
      stiffness: 400,
      damping: 40
    }
  }
};

export const Example = () => {
  const [isOpen, toggleOpen] = useCycle(false, true);
  const containerRef = useRef(null);
  const { height } = useDimensions(containerRef);

  return (
    <>
      <motion.nav
        initial={false}
        animate={isOpen ? "open" : "closed"}
        custom={height}
        ref={containerRef}
        className="nav"
      >
        <motion.div className="background" variants={sidebar} />
        <Navigation />

        <MenuToggle toggle={() => toggleOpen()} />
      </motion.nav>
      s
      <section className="homesection">
        <div className="homewrapper">
          <div className="hometitle">
            Looking <br />
            for a <br />
            React <br />
            Developer?
            <div className="btncont">
              <button className="custom-btn btn-12">
                <span>3 CV's FR-NL-EN</span>

                <span>
                  <img className="dwl" src={dwl} alt="" />
                  Download CV
                </span>
              </button>
            </div>
          </div>
        </div>

        <div className="homecontainer">
          <img className="homeimage" src={beneface} alt="beneface" />
        </div>
      </section>
      <section className="aboutsection">
        <div className="hometitle">
          Looking <br />
          for a <br />
          React <br />
          Developer?
        </div>
        <div className="aboutcontainer">
          <img className="homeimage" src={klas} alt="beneface" />
        </div>
      </section>
    </>
  );
};
