import React, { useState } from "react";
import styled from "styled-components";
import { FaTimes } from "react-icons/fa";
import "./styles.css"
const SidebarLink = styled.a`
  color: #000;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  text-decoration: none;
  list-style: none;
  transition: 0.2s ease-in-out;
  cursor: pointer;
  &:hover {
    color: #e31837;
    font-weight: bold;
    transition: 0.2s ease-in-out;
    transform: scale(1.3);
  }
`;
const CloseIcon = styled(FaTimes)`
  color: #000;
  transition: 0.2s ease-in-out;

  &:hover {
    color: #e31837;
    transition: 0.2s ease-in-out;
    transform: scale(1.3);
  }
`;


const Icon = styled.div`
  position: absolute;
  top: 1.2rem;
  right: 1.5rem;
  background: transparent;
  border: transparent;
  font-size: 2rem;
  cursor: pointer;
  outline: none;
`;


const SideBtnWrap = styled.div`
  display: flex;
  justify-content: center;
`;

const Siderbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div>
      <Icon onClick={toggle}>
        <CloseIcon />
      </Icon>
      <div toggle={toggle} />
      {/* <div isOpen={isOpen} toggle={toggle} /> */}



    </div>
  );
};

export default Siderbar;
