import React, { useEffect, useState } from "react";
import "./PFolio.css";
import images from "./data";
import { SRLWrapper } from "simple-react-lightbox";
import styled from "styled-components";
import TheEyebtn from "./eyebtn";
import eye from "./eye.png";

const Eyebtn = styled.div`
  position: relative;
  background: #f16603;
  z-index: 100;
  text-align: center;
  color: #fff;
  margin: 25px 25px 0px 25px;
  width: 5vw;
  border-radius: 5px;
  box-shadow: rgba(60, 64, 67, 0.3) 0px 1px 2px 0px,
    rgba(60, 64, 67, 0.15) 0px 2px 3px 2px;
`;
const Eyeimg = styled.img`
width: 2v
`;
const ImageCard = styled.div`
  position: relative;
  z-index: 100;
  margin: 25px;
  border-radius: 5px;
  padding: 0px;
  box-shadow: rgba(60, 64, 67, 0.3) 0px 1px 2px 0px,
    rgba(60, 64, 67, 0.15) 0px 2px 3px 2px;
`;
const Title = styled.div`
  font-weight: bold;
  margin-top: 5vw;
  display: inline;
  justify-content: center;
  text-align: center;
  /* color: #cec950; */
  color: #fff;
  font-size: calc(10px + 1vw);
  position: relative;
  width: 100%;
  text-shadow: rgba(60, 64, 67, 0.3) 0px 1px 2px 0px,
    rgba(60, 64, 67, 0.15) 0px 2px 3px 2px;
`;
const Softtitle = styled.div`
  color: #cec950;
  font-weight: bold;
  font-size: calc(5px + 0.5vw);
  display: inline;
  justify-content: center;
  text-align: center;
`;
const Softtext = styled.div`
  color: #fff;
  margin-top: 2vw;
  font-weight: bold;
  font-size: calc(5px + 0.5vw);
  display: inline;
  justify-content: center;
  text-align: center;
`;
const Softdate = styled.div`
  color: #fff;
  font-size: calc(5px + 0.5vw);
  display: inline;
  margin-top: 2vw;
  justify-content: center;
  text-align: center;
`;

const Overlay = styled.div`
  position: absolute;
  bottom: 100%;
  left: 0;
  display: flex;
  flex-direction: column;
  border-radius: 5px;
  right: 0;
  background: rgba(0, 172, 238, 0.9);
  background: linear-gradient(
    0deg,
    rgba(0, 126, 251, 0.9) 0%,
    rgba(0, 212, 250, 0.9) 100%
  );
  overflow: hidden;
  width: 100%;
  height: 0%;
  transition: 0.5s ease;
`;

const options = {
  // settings: {
  // 	overlayColor: 'rgb(25, 136, 124)',
  // 	autoplaySpeed: 1500,
  // 	transitionSpeed: 900
  // },
  // buttons: {
  // 	backgroundColor: 'red',
  // 	iconColor: 'rgba(126, 172, 139, 0.8)'
  // },
  // caption: {
  // 	captionColor: '#a6cfa5',
  // 	captionFontFamily: 'Raleway, sans-serif',
  // 	captionFontWeight: '300',
  // 	captionTextTransform: 'uppercase'
  // },
  // progressBar: {
  // 	height: '20px',
  // 	fillColor: 'blue',
  // 	backgroundColor: 'white'
  // }
};

function PFolio() {
  const [tag, setTag] = useState("GIT");
  const [filteredImages, setFilteredImages] = useState([]);

  useEffect(() => {
    tag === "GIT"
      ? setFilteredImages(images)
      : setFilteredImages(images.filter(image => image.tag.includes(tag)));
  }, [tag]);

  return (
    <div className="App">
      <div className="tags">
        <TagButton
          name="React"
          tagActive={tag === "React" ? true : false}
          handleSetTag={setTag}
        />
        <TagButton
          name="React-Native"
          tagActive={tag === "React-Native" ? true : false}
          handleSetTag={setTag}
        />{" "}
        <TagButton
          name="GIT"
          tagActive={tag === "GIT" ? true : false}
          handleSetTag={setTag}
        />
        <TagButton
          name="FireBase"
          tagActive={tag === "FireBase" ? true : false}
          handleSetTag={setTag}
        />
        <TagButton
          name="Latest Job"
          tagActive={tag === "Latest Job" ? true : false}
          handleSetTag={setTag}
        />
      </div>
      <SRLWrapper options={options}>
        <div className="PFcontainer">
          {" "}
          {filteredImages.map(image => (
            <>
              <ImageCard key={image.id} className="imageCard">
                <a className="imageatag" href={`/images/${image.imageName}`}>
                  <img
                    className="image"
                    src={`/images/${image.imageName}`}
                    alt=""
                  />{" "}
                  <Overlay className="overlay">
                    <Eyebtn>
                      <Eyeimg src={eye} alt="eye" />
                      App</Eyebtn>

                    <Title>{image.title}</Title>
                    <Softtitle>{image.software}</Softtitle>
                    <Softtext>{image.description}</Softtext>
                    <Softdate>{image.date}</Softdate>
                  </Overlay>
                </a>
              </ImageCard>{" "}
            </>
          ))}
        </div>
      </SRLWrapper>
    </div>
  );
}

const TagButton = ({ name, handleSetTag, tagActive }) => {
  return (
    <button
      className={`tag ${tagActive ? "active" : null}`}
      onClick={() => handleSetTag(name)}
    >
      {name.toUpperCase()}
    </button>
  );
};

export default PFolio;
