import React, { useState } from "react";
import styled from "styled-components";
import { FaTimes } from "react-icons/fa";
import "./styles.css"
const SidebarLink = styled.a`
  color: #000;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  text-decoration: none;
  list-style: none;
  transition: 0.2s ease-in-out;
  cursor: pointer;
  &:hover {
    color: #e31837;
    font-weight: bold;
    transition: 0.2s ease-in-out;
    transform: scale(1.3);
  }
`;
const CloseIcon = styled(FaTimes)`
  color: #000;
  transition: 0.2s ease-in-out;

  &:hover {
    color: #e31837;
    transition: 0.2s ease-in-out;
    transform: scale(1.3);
  }
`;
const SidebarContainer = styled.div`
  position: fixed;
  z-index: 999;
  width: 350px;
  height: 100%;
  background-color: rgba(255, 255, 255, 0.5);
  display: grid;
  align-items: center;
  top: 0;
  backdrop-filter: blur(8px);
  -webkit-backdrop-filter: blur(8px);
  transition: 0.3s ease-in-out;
  /* right: ${({ isOpen }) => (isOpen ? "0" : "-1000px")}; */
  box-shadow: rgba(60, 64, 67, 0.3) 0px 10px 20px 0px,
    rgba(60, 64, 67, 0.15) 0px 20px 30px 2px;
  @media screen and (max-width: 400px) {
    width: 100%;
  }
`;

const Icon = styled.div`
  position: absolute;
  top: 1.2rem;
  right: 1.5rem;
  background: transparent;
  border: transparent;
  font-size: 2rem;
  cursor: pointer;
  outline: none;
`;

const SidebarMenu = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  grid-template-rows: repeat(3, 80px);
  text-align: center;

  @media screen and (max-width: 480px) {
    grid-template-rows: repeat(3, 60px);
  }
`;

const SidebarWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  background: #e31837;
  padding: 16px 64px;
  border: none;
  cursor: pointer;
  transition: 0.2s ease-in-out;
  &:hover {
    transition: 0.2s ease-in-out;
    background: #fff;
  }
`;
// const SidebarLink = styled(Link)`
//   display: flex;
//   align-items: center;
//   justify-content: center;
//   font-size: 1.5rem;
//   text-decoration: none;
//   list-style: none;
//   transition: 0.2s ease-in-out;
//   color: #000;
//   cursor: pointer;

//   &:hover {
//     color: #e31837;
//     transition: 0.2s ease-in-out;
//   }
// `;

const SideBtnWrap = styled.div`
  display: flex;
  justify-content: center;
`;

const Siderbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => {
    setIsOpen(!isOpen);
  };

  return (
    <SidebarContainer>
      <Icon onClick={toggle}>
        <CloseIcon />
      </Icon>
      <div toggle={toggle} />
      {/* <div isOpen={isOpen} toggle={toggle} /> */}

      <SidebarLink href="#">Home</SidebarLink>
      <SidebarLink href="#">About Me</SidebarLink>
      <SidebarLink href="#">Projects</SidebarLink>
      <SidebarWrapper>
        <SidebarLink id="contactwhite" href="#">Contact</SidebarLink>
      </SidebarWrapper>
    </SidebarContainer>
  );
};

export default Siderbar;
